declare(strict_types=1);

namespace Zikula\PagesModule\HookSubscriber\Base;

use Symfony\Contracts\Translation\TranslatorInterface;
use Zikula\Bundle\HookBundle\Category\FormAwareCategory;
use Zikula\Bundle\HookBundle\HookSubscriberInterface;

/**
 * Base class for form aware hook subscriber.
 */
abstract class AbstractPageFormAwareHookSubscriber implements HookSubscriberInterface
{
    /**
     * @var TranslatorInterface
     */
    protected $translator;

    public function __construct(TranslatorInterface $translator)
    {
        $this->translator = $translator;
    }

    public function getOwner(): string
    {
        return 'ZikulaPagesModule';
    }
    
    public function getCategory(): string
    {
        return FormAwareCategory::NAME;
    }
    
    public function getTitle(): string
    {
        return $this->translator->trans('Page form aware subscriber', [], 'hooks');
    }
    
    public function getAreaName(): string
    {
        return 'subscriber.zikulapagesmodule.form_aware_hook.pages';
    }

    public function getEvents(): array
    {
        return [
            // Display hook for create/edit forms.
            FormAwareCategory::TYPE_EDIT => 'zikulapagesmodule.form_aware_hook.pages.edit',
            // Process the results of the edit form after the main form is processed.
            FormAwareCategory::TYPE_PROCESS_EDIT => 'zikulapagesmodule.form_aware_hook.pages.process_edit'
        ];
    }
}
